class WAPT_ComponentsPage extends \WBCR\Factory_Templates_132\Pages\Components {

	/**
	 * {@inheritdoc}
	 *
	 * @author Alexander Kovalev <alex.kovalevv@gmail.com>
	 * @since  1.6.0
	 * @var string
	 */
	public $id = 'apt_components';


	public function get_components()
	{
		$default_image = 'data:image/svg+xml;base64,';

		$components = [];
		$base_path_prefix = defined( 'BUILD_TYPE' ) ? 'wp-plugin-' : '';

		$components = array_merge($components, [
			[
				'name' => 'ai_image_generator',
				'title' => __('Ai image generator', 'apt'),
				'url' => 'https://wordpress.org/plugins/ai-image-generator/',
				'type' => 'wordpress',
				'build' => 'freemium',
				'base_path' => $base_path_prefix . 'ai-image-gen/ai-image-gen.php',
				'icon' =>  WAPT_PLUGIN_URL . '/admin/assets/img/aig-icon-128x128.png',
				'description' => __('Automatically generates article thumbnails from text, using all the features of the Chatgpt and Dalle neural networks.', 'clearfy')
			],

			[
				'name' => 'robin_image_optimizer',
				'title' => __('Robin image optimizer', 'clearfy'),
				'url' => 'https://wordpress.org/plugins/robin-image-optimizer/',
				'type' => 'wordpress',
				'build' => 'freemium',
				'base_path' => $base_path_prefix . 'robin-image-optimizer/robin-image-optimizer.php',
				'icon' => WAPT_PLUGIN_URL . '/admin/assets/img/rio-icon-128x128.png',
				'description' => __('Automatic image optimization without any quality loss. No limitations, no paid plans. The best Wordpress image optimization plugin allows optimizing any amount of images for free!', 'clearfy')
			],
		]);



		return $components;
	}
}
